import React, {useState, useEffect} from 'react';
import axios from 'axios';
import * as Yup from 'yup';




const PizzaForm = () => {

    const [post, setPost] = useState([])

    


    const [inputForm, setInputForm] = useState({name: "", psize: "", toppings: "", instructions: ""})
    
    
    const handleSubmit = event => {

        event.preventDefault();
        // setInputForms([...inputForms, { ...inputForm, id: Date.now() }]);
        axios.post("https://reqres.in/api/users", inputForm)
        .then(res => {setPost(res.data)
        
           setInputForm({

            name: "", 
            psize: "",
            toppings: "",
            instructions: ""

           }) ;
        }).catch(err => console.log(err.res));
    }; 



    const formSchema = Yup.object().shape({
       name: Yup
       .string()
       .required("Must input a Name."),
        psize: Yup
          .string()
          .required("Must choose a size."),
        toppings: Yup
          .boolean(),
        instructions: Yup
          .string()
          
      });

      const [isButtonDisabled, setIsButtonDisabled] = useState(true); 
    
      const [errors, setErrors] = useState({
        name: "",
        psize: "",
        toppings: "",
        instructions: ""
      });

      


      const inputChange = event => {
       
        event.persist();

        const newFormData = {

            ...inputForm,
            [event.target.name]: event.target.type === "checkbox" ? event.target.checked : event.target.value

        };
        validateChange(event);
        setInputForm(newFormData);
    
    };

    useEffect(() => {

        formSchema.isValid(inputForm)
        .then(valid => {

            setIsButtonDisabled(!valid);
        });

    }, [inputForm]);
    

    const validateChange = event => {

        Yup.reach(formSchema, event.target.name)
        .validate(event.target.value)
        .then( valid => {

           setErrors({...errors, [event.target.name]: ""}) 
        })
        .catch(err => setErrors({...errors, [event.target.name]: err.errors[0] }));
    }




    return (

        <form onSubmit={handleSubmit}>

            <label htmlFor="name" >
                Name:
            <input id="name" type="text" name="name" value={inputForm.name} onChange={inputChange} />
            </label> 
           
            <label htmlFor="psize"> 
             Pizza Size:
            <select id="psize" name="psize" >
            <options  value="Personal"  >Personal</options>
            <options  value="Small" >Small</options>
            <options  value="Medium">Medium</options>
            <options  value="Large" >Large</options>
            <options  value="Xtralarge" >Xtra Large</options>
            </select>
            </label>
           
            <label htmlFor="toppings">
                Toppings:
                Plain
            <input type="radio"  value="Plain" onChange={inputChange} />
            Pepporoni
            <input type="radio"    value="Pepperoni" onChange={inputChange} />
            Cheese
            <input type="radio"   value="Cheese" onChange={inputChange} />
            Mushrooms
            <input type="radio"   value="Mushrooms" onChange={inputChange} />
            </label>
           
            <label htmlFor="instructions">
                Special instructions:
            <textarea id="instructions" type="text" name="instructions" value={inputForm.instructions} onChange={inputChange} />
            </label>
           
            <button >Order Now</button>

            <label htmlFor="positions">
    What would you like to help with?
    <select id="positions" name="positions">
      <option value="Newsletter">Newsletter</option>
      <option value="Yard Work">Yard Work</option>
      <option value="Administrative Work">Administrative Work</option>
      <option value="Tabling">Tabling</option>
    </select>
  </label>

        </form>


    )


} 

export default PizzaForm;
